const pad = (n) => String(n).padStart(2, '0');
const toKey = (d) => `${d.getFullYear()}-${pad(d.getMonth() + 1)}-${pad(d.getDate())}`;
const timeToMin = (t) => {
	const [h, m] = t.split(':').map(Number);
	return h * 60 + m;
};

// Helper function to create a unique ID for template tasks
function getTemplateId(task) {
	return `${task.title.replace(/\s+/g, '-')}-${task.start}`;
}

/**
 * Safely parses data that might be a JSON string or already a JS object.
 * @param {*} data The data to parse.
 * @param {*} defaultValue The value to return on failure.
 * @returns {object|Array} The parsed object or the default value.
 */
const safeJsonParse = (data, defaultValue) => {
	if (typeof data === 'object' && data !== null) {
		return data; // It's already a valid object/array.
	}
	if (typeof data === 'string' && data) {
		try {
			return JSON.parse(data); // It's a string, so parse it.
		} catch (e) {
			return defaultValue; // The string is invalid JSON.
		}
	}
	return defaultValue; // It's null, undefined, or something else.
};

// Task factory for template tasks
function taskFromTemplate(templateTask) {
	return {
		id: uid(), // A temporary ID for the DOM, not for storage
		templateId: getTemplateId(templateTask), // The stable ID for storage
		title: templateTask.title,
		start: templateTask.start,
		end: templateTask.end,
		done: false,
		type: 'template',
	};
}

// ================================================================
// END: SHARED SERVER-SIDE UTILITIES
// ================================================================

/**
 * SERVER-SIDE equivalent of the client's getDayTasks function.
 * It reconstructs the full task list for a given day from raw Redis data.
 */
// REPLACE your existing serverGetDayTasks function with this entire new version.
/**
 * SERVER-SIDE equivalent of the client's getDayTasks function.
 * This robust version safely handles data that may have been auto-parsed by Upstash Redis.
 */
function serverGetDayTasks(dayKey, templatesData, dailyModsData) {
	const date = new Date(dayKey.replace(/-/g, '/'));
	const weekday = date.getDay();

	// 1. Get base templates safely.
	const baseTemplate = safeJsonParse(templatesData?.[weekday], []);

	// 2. Get daily modifications safely.
	const dayData = {
		mods: safeJsonParse(dailyModsData?.mods, {}),
		added: safeJsonParse(dailyModsData?.added, []),
	};

	// 3. Process template tasks, applying modifications
	const templateTasks = baseTemplate
		.map((tpl) => {
			const task = taskFromTemplate(tpl);
			const mod = dayData.mods[task.templateId];

			if (mod) {
				if (mod.deleted) return null;
				task.done = mod.done || false;
			}
			return task;
		})
		.filter(Boolean);

	// 4. Get custom tasks
	const customTasks = dayData.added.map((t) => ({ ...t }));

	// 5. Combine and return
	return [...templateTasks, ...customTasks];
}

/**
 * A robust helper to safely read and parse a JSON value from a Redis hash field.
 * This version handles cases where the @upstash/redis client has already auto-parsed the JSON.
 * @param {Redis} redis - The Redis client instance.
 * @param {string} key - The Redis key for the hash.
 * @param {string} field - The field within the hash.
 * @param {any} defaultValue - The value to return if the field doesn't exist or is invalid.
 * @returns {Promise<any>} The parsed object or the default value.
 */
async function getRedisJSON(redis, key, field, defaultValue) {
	const data = await redis.hget(key, field);

	// Handles null, undefined, etc.
	if (data === null || typeof data === 'undefined') {
		return defaultValue;
	}

	// If Upstash already parsed it into an object/array, return it directly.
	if (typeof data === 'object') {
		return data;
	}

	// If it's a string, try to parse it.
	if (typeof data === 'string') {
		// Handle empty string case specifically
		if (data === '') {
			return defaultValue;
		}
		try {
			return JSON.parse(data);
		} catch (e) {
			// This log is now for legitimate parsing errors of non-empty strings.
			console.error(`Invalid JSON string in Redis. Key: ${key}, Field: ${field}. Falling back to default.`, data);
			return defaultValue;
		}
	}

	// For any other unexpected type (like a number), fall back to default.
	console.warn(`Unexpected data type in Redis. Key: ${key}, Field: ${field}. Type: ${typeof data}. Falling back to default.`);
	return defaultValue;
}

