                updateTaskOnServer({ dayKey, taskId, taskType, templateId, isDone, start });

                if (isDone && taskTitle) {
                  sendTelegramMessage(' Completed: ' + taskTitle);
                  const uniqueTaskKey = dayKey + '-' + (taskId || templateId);
                  sentNotifications.set(uniqueTaskKey + '-completed', true);
                }
