export default {
	async fetch(request, env, ctx) {
		const url = new URL(request.url);
		const path = url.pathname;

		// ============================================================
		// 1. PUBLIC ROUTES (Accessible by Bots & Non-Logged In Users)
		// ============================================================

		// Telegram Webhook
		if (path === '/api/telegram/webhook') {
			if (request.method === 'POST') {
				await handleTelegramWebhook(request, env);
				return new Response('OK');
			}
			return new Response('Method Not Allowed', { status: 405 });
		}

		// PWA Manifest & Service Worker
		if (path === '/manifest.json') return handleManifestRequest(request);
		if (path === '/sw.js') return handleServiceWorkerRequest(request);

		// *** SOCIAL MEDIA ICONS (Now Public) ***
		if (path === '/icon-192x192.png' || path === '/icon-512x512.png') {
			return handleIconRequest(ICON_PNG_B64);
		}

		// ============================================================
		// 2. AUTHENTICATION CHECK
		// ============================================================

		const cookie = request.headers.get('Cookie');
		const session = await getSession(cookie, env.JWT_SECRET);

		// Debug Logging
		if (session) {
			console.log('[DEBUG] Session VALID. User ID:', session.user.id);
		} else {
			console.log('[DEBUG] Session INVALID or MISSING.');
		}

		// --- AUTHENTICATION FLOW ---
		if (!session) {
			if (path === '/auth/google') return handleGoogleLogin(env);
			if (path === '/auth/google/callback') return handleGoogleCallback(request, env);
			// Serve the Login Page for all other requests
			return new Response(LOGIN_PAGE_HTML, { headers: { 'Content-Type': 'text/html' } });
		}

		// ============================================================
		// 3. LOGGED-IN USER ROUTES
		// ============================================================

		switch (path) {
			case '/':
				// Serve the main application
				return serveApp(session.user, env);

			case '/api/task/add':
				if (request.method === 'POST') {
					const taskData = await request.json();
					const { newId } = await handleAddTask(session.user.id, taskData, env);
					return new Response(JSON.stringify({ success: true, newId }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/user/onboarding/complete':
				if (request.method === 'POST') {
					const redis = Redis.fromEnv(env);
					await redis.hset(`user:${session.user.id}:profile`, { onboardingComplete: 'true' });
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/user/telegram/update':
				if (request.method === 'POST') {
					const { telegramId } = await request.json();
					await handleUpdateTelegramId(session.user.id, telegramId, env);
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/task/update':
				if (request.method === 'POST') {
					const updateData = await request.json();
					await handleTaskUpdate(session.user.id, updateData, env);
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/task/delete':
				if (request.method === 'POST') {
					const deleteData = await request.json();
					await handleTaskDelete(session.user.id, deleteData, env);
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/day/clear':
				if (request.method === 'POST') {
					const clearData = await request.json();
					await handleClearDay(session.user.id, clearData, env);
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/api/routines/update':
				if (request.method === 'POST') {
					const routineData = await request.json();
					await handleUpdateRoutines(session.user.id, routineData, env);
					return new Response(JSON.stringify({ success: true }), { headers: { 'Content-Type': 'application/json' } });
				}
				return new Response('Method Not Allowed', { status: 405 });

			case '/logout':
				return new Response(null, {
					status: 302,
					headers: {
						Location: '/',
						'Set-Cookie': 'session=; Path=/; Expires=Thu, 01 Jan 1970 00:00:00 GMT; HttpOnly; Secure; SameSite=Lax',
					},
				});

			default:
				if (path.startsWith('/auth/')) {
					return new Response(null, { status: 302, headers: { Location: '/' } });
				}
				return new Response('Not Found', { status: 404 });
		}
	},
	// ================================================================
	// 2. ADD THIS NEW 'scheduled' HANDLER FOR THE CRON JOB
	// ================================================================
	// REPLACE your existing 'scheduled' handler with this entire block
	async scheduled(controller, env, ctx) {
		// 1. Robust Check: Ensure today's index exists.
		// If the worker was down at 00:00, this fixes it immediately.
		await ensureDailyIndexingIsDone(env);

		// 2. The Announcer: Check for tasks starting *right now*
		await handleNotifications(env);
	},
};

