/**
 * The "Announcer" - runs every minute.
 * Checks the index to see who needs a notification right now.
 */
// REPLACE your entire handleNotifications function with this new version.
// The other functions (handleDailyIndexing, processUserNotification, etc.) do NOT need to change.

/**
 * The "Announcer" - runs every minute.
 * Checks the index for tasks starting in the NEXT MINUTE to build a buffer
 * against cron execution delays.
 */
async function handleNotifications(env) {
	const redis = Redis.fromEnv(env);

	// 1. Get the current time when the cron actually runs.
	const nowUTC = new Date();

	// 2. *** THE KEY CHANGE ***
	// Calculate the time for the beginning of the NEXT minute.
	const nextMinuteUTC = new Date(nowUTC.getTime() + 60 * 1000); // Add 60 seconds

	// 3. Convert that future time to IST for our key.
	const nextMinuteIST = new Date(nextMinuteUTC.getTime() + 5.5 * 60 * 60 * 1000);

	// 4. Construct the key using the future IST time.
	const dayKey = toKey(nextMinuteIST);
	const timeKey = `${pad(nextMinuteIST.getHours())}:${pad(nextMinuteIST.getMinutes())}`;
	const indexKey = `schedule_index:${dayKey}:${timeKey}`;

	// Add logging to see the calculation in action
	console.log(`[CRON RUN] Current IST: ${new Date(nowUTC.getTime() + 5.5 * 60 * 60 * 1000).toISOString()}`);
	console.log(`[CRON CHECK] Checking for tasks starting in the next minute at ${timeKey} IST`);
	console.log(`[CRON CHECK] Querying index key: ${indexKey}`);

	const userIdsToNotify = await redis.smembers(indexKey);
	if (!userIdsToNotify || userIdsToNotify.length === 0) {
		console.log(`[CRON RESULT] No tasks found for ${timeKey} IST.`);
		return; // Job is done.
	}

	console.log(`[CRON RESULT] Found user(s) for ${timeKey} IST:`, userIdsToNotify);

	const promises = userIdsToNotify.map((userId) =>
		// We pass the FUTURE timeKey to processUserNotification, so it knows which tasks to find.
		processUserNotification(userId, dayKey, timeKey, env, redis),
	);
	await Promise.all(promises);
	console.log('[CRON] Notification job finished for this minute.');
}

/**
 * ROBUST INDEXER: Checks if today's index exists. If not, creates it.
 * This runs every minute but only acts once a day.
 */
async function ensureDailyIndexingIsDone(env) {
	const redis = Redis.fromEnv(env);

	// 1. Calculate Today's Key in IST
	const nowIST = new Date(new Date().getTime() + 5.5 * 60 * 60 * 1000);
	const dayKey = toKey(nowIST); // e.g., "2025-11-22"

	// 2. CHECK: Has this day been indexed successfully?
	const statusKey = `system:index_status:${dayKey}`;
	const status = await redis.get(statusKey);

	// FIX: Check for the specific string "DONE" to avoid JSON boolean parsing issues
	if (status === 'DONE') {
		return;
	}

	// 3. LOCK
	const lockKey = `lock:indexing:${dayKey}`;
	const acquiredLock = await redis.set(lockKey, 'locked', { nx: true, ex: 600 });

	if (!acquiredLock) {
		console.log('[Robust-Indexer] Another worker is building the index. Skipping.');
		return;
	}

	// 4. BUILD
	console.log(`[Robust-Indexer] Index missing for ${dayKey}. Starting build...`);

	try {
		await handleDailyIndexing(env, nowIST);

		// FIX: Save "DONE" instead of "true"
		await redis.set(statusKey, 'DONE', { ex: 172800 });
		console.log(`[Robust-Indexer] Successfully built and marked index for ${dayKey}.`);
	} catch (error) {
		console.error(`[Robust-Indexer] Failed to build index:`, error);
	} finally {
		await redis.del(lockKey);
	}
}

/**
 * The "Night Shift Secretary" - runs once daily.
 * Pre-populates the index for tomorrow's routine tasks.
 */
/**
 * The Worker Function. It accepts a Date object so it knows exactly what to index.
 */
async function handleDailyIndexing(env, targetDateIST) {
	const redis = Redis.fromEnv(env);

	// Use the passed date, not "new Date()" inside here.
	const dayKey = toKey(targetDateIST);
	const dayOfWeek = targetDateIST.getDay();

	console.log(`[Night-Shift] Processing users for ${dayKey} (Day ${dayOfWeek})`);

	const userIds = await redis.smembers('users');
	if (!userIds || userIds.length === 0) return;

	for (const userId of userIds) {
		const templatesKey = `user:${userId}:templates`;
		const templateJson = await redis.hget(templatesKey, dayOfWeek);

		if (!templateJson) continue;
		const tasks = safeJsonParse(templateJson, []);
		if (tasks.length === 0) continue;

		const pipeline = redis.pipeline();

		// OPTIONAL SAFETY: Clear existing index for this user/day before adding
		// This makes the script "Idempotent" (safe to run twice).
		// But since we use SADD (Set Add), it handles duplicates automatically.

		for (const task of tasks) {
			const indexKey = `schedule_index:${dayKey}:${task.start}`;
			pipeline.sadd(indexKey, userId);
			pipeline.expire(indexKey, 172800);
		}
		await pipeline.exec();
	}
}

/**
 * Fetches task details for a specific user and sends the notification.
 */
async function processUserNotification(userId, dayKey, timeKey, env, redis) {
	const templatesKey = `user:${userId}:templates`;
	const dailyModsKey = `user:${userId}:mods:${dayKey}`;
	const [templatesData, dailyModsData] = await redis.pipeline().hgetall(templatesKey).hgetall(dailyModsKey).exec();
	const allTasks = serverGetDayTasks(dayKey, templatesData, dailyModsData);

	const tasksToNotify = allTasks.filter((task) => task.start === timeKey && !task.done);
	// --- ADD LOG ---
	console.log(`[PROCESS USER ${userId}] Found ${tasksToNotify.length} tasks matching time ${timeKey}`);
	// --- END LOG --
	if (tasksToNotify.length === 0) return;

	const telegramId = await redis.hget(`user:${userId}:profile`, 'telegramId');
	// --- ADD LOG ---
	console.log(`[PROCESS USER ${userId}] Retrieved Telegram ID: ${telegramId}`);
	// --- END LOG ---

	if (!telegramId) return;

	// Helper for 12-hour format (Server-Side)
	const to12Hour = (t) => {
		let [h, m] = t.split(':').map(Number);
		const suffix = h >= 12 ? 'PM' : 'AM';
		h = h % 12 || 12;
		return `${h}:${String(m).padStart(2, '0')} ${suffix}`;
	};

	for (const task of tasksToNotify) {
		// ---------------------------------------------------------
		// ONE-LINER NOTIFICATION FORMAT
		// Example:  *Meeting* (2:00 PM - 3:00 PM)
		// ---------------------------------------------------------
		const message = ` *${task.title}*\n (${to12Hour(task.start)} - ${to12Hour(task.end)})`;
		// ---------------------------------------------------------
		const lockKey = `notify_lock:${userId}:${dayKey}:${task.templateId || task.id}`;
		const lock = await redis.set(lockKey, '1', { ex: 120, nx: true });

		if (lock) {
			// --- ADD LOG ---
			console.log(`[SENDING] Preparing to send notification for "${task.title}" to Chat ID ${telegramId}`);
			// --- END LOG ---
			await sendTelegramNotification(env.TELEGRAM_BOT_TOKEN, telegramId, message);
			console.log(`[CRON] Sent notification to user ${userId} for task "${task.title}".`);
		}
	}
}

/**
 * Sends a message via the Telegram Bot API.
 */
async function sendTelegramNotification(botToken, chatId, text) {
	const url = `https://api.telegram.org/bot${botToken}/sendMessage`;
	try {
		const response = await fetch(url, {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({ chat_id: chatId, text: text, parse_mode: 'Markdown' }),
		});
		if (!response.ok) {
			console.error(`[Telegram API] Failed to send message. Status: ${response.status}`);
		}
	} catch (error) {
		console.error(`[Telegram API] Network error:`, error);
	}
}

