/**
 * Adds a new custom task to a specific day using a Redis transaction to prevent race conditions.
 */
async function handleAddTask(userId, { dayKey, title, start, end }, env) {
	const redis = Redis.fromEnv(env);
	const dailyModsKey = `user:${userId}:mods:${dayKey}`;
	const newId = uid();
	const newTask = { id: newId, title, start, end, done: false, type: 'custom' };

	// Use a transaction to safely read, modify, and write the 'added' array.
	const result = await redis.multi().hget(dailyModsKey, 'added').exec();

	const currentAddedData = result[0];
	let added = safeJsonParse(currentAddedData, []);

	if (typeof currentAddedData === 'string' && currentAddedData) {
		try {
			added = JSON.parse(currentAddedData);
		} catch (e) {}
	} else if (typeof currentAddedData === 'object' && currentAddedData !== null) {
		added = currentAddedData;
	}

	added.push(newTask);
	added.sort((a, b) => (a.start > b.start ? 1 : -1));

	await redis.hset(dailyModsKey, { added: JSON.stringify(added) });
	await redis.expire(dailyModsKey, 259200); // Set/reset TTL to 72 hours

	// --- NEW: TIME CHECK LOGIC ---
	// Only add to notification index if the task is in the future!

	// 1. Get Current IST Time
	const nowUTC = new Date();
	const nowIST = new Date(nowUTC.getTime() + 5.5 * 60 * 60 * 1000);

	const currentDayKey = toKey(nowIST);
	const currentMin = nowIST.getHours() * 60 + nowIST.getMinutes();
	const taskMin = timeToMin(start);

	let isFuture = false;

	if (dayKey > currentDayKey) {
		isFuture = true; // It's tomorrow or later
	} else if (dayKey === currentDayKey) {
		if (taskMin > currentMin) {
			isFuture = true; // It's today, and time hasn't passed yet
		}
	}

	if (isFuture) {
		const indexKey = `schedule_index:${dayKey}:${start}`;
		const pipeline = redis.pipeline();
		pipeline.sadd(indexKey, userId);
		pipeline.expire(indexKey, 172800); // Set to expire in 48 hours
		await pipeline.exec();
		console.log(`[AddTask] Indexed future task "${title}" at ${start}`);
	} else {
		console.log(`[AddTask] Skipped indexing past task "${title}" at ${start}`);
	}
	// --- END TIME CHECK LOGIC ---

	return { newId };
}

