async function handleTaskUpdate(userId, { dayKey, taskId, taskType, templateId, isDone, start }, env) {
	const redis = Redis.fromEnv(env);
	const dailyModsKey = `user:${userId}:mods:${dayKey}`;
	const templatesKey = `user:${userId}:templates`;

	// Ensure UserID is a string for Redis consistency
	const uidStr = String(userId);

	// 1. Update Data (JSON)
	if (taskType === 'custom') {
		const added = await getRedisJSON(redis, dailyModsKey, 'added', []);
		const taskIndex = added.findIndex((t) => t.id === taskId);
		if (taskIndex > -1) {
			added[taskIndex].done = isDone;
			await redis.hset(dailyModsKey, { added: JSON.stringify(added) });
			await redis.expire(dailyModsKey, 259200);
		}
	} else if (taskType === 'template') {
		const mods = await getRedisJSON(redis, dailyModsKey, 'mods', {});
		if (!mods[templateId]) mods[templateId] = {};
		mods[templateId].done = isDone;
		if (isDone) delete mods[templateId].deleted;
		await redis.hset(dailyModsKey, { mods: JSON.stringify(mods) });
		await redis.expire(dailyModsKey, 259200);
	}

	// 2. Update Notification Index
	if (start) {
		const indexKey = `schedule_index:${dayKey}:${start}`;

		if (isDone) {
			// CHECK BEFORE REMOVE
			// Fetch fresh data to see if ANY tasks at this time are still active
			const [templatesData, dailyModsData] = await redis.pipeline().hgetall(templatesKey).hgetall(dailyModsKey).exec();

			const allTasks = serverGetDayTasks(dayKey, templatesData, dailyModsData);

			// Filter: Same Start Time AND Not Done
			const remaining = allTasks.filter((t) => t.start === start && !t.done);

			if (remaining.length === 0) {
				await redis.srem(indexKey, uidStr);
				console.log(`[Tick] Clear: No active tasks left at ${start}. Removed user.`);
			} else {
				console.log(`[Tick] Hold: ${remaining.length} tasks still active at ${start}.`);
			}
		} else {
			// UNTICKING (Restore Logic)
			const nowUTC = new Date();
			const nowIST = new Date(nowUTC.getTime() + 5.5 * 60 * 60 * 1000);
			const currentDayKey = toKey(nowIST);
			const currentMin = nowIST.getHours() * 60 + nowIST.getMinutes();
			const taskMin = timeToMin(start);

			let isFuture = false;
			if (dayKey > currentDayKey) isFuture = true;
			else if (dayKey === currentDayKey && taskMin > currentMin) isFuture = true;

			if (isFuture) {
				let shouldIndex = false;
				// Index Custom tasks OR Today's Templates
				if (taskType === 'custom') shouldIndex = true;
				else if (dayKey === currentDayKey) shouldIndex = true;

				if (shouldIndex) {
					const pipeline = redis.pipeline();
					pipeline.sadd(indexKey, uidStr);
					pipeline.expire(indexKey, 172800);
					await pipeline.exec();
					console.log(`[Untick] Restored index for ${start}`);
				}
			}
		}
	}
}

/**
 * Deletes a task from a specific day.
 */

async function handleTaskDelete(userId, { dayKey, taskId, taskType, templateId, start }, env) {
	const redis = Redis.fromEnv(env);
	const dailyModsKey = `user:${userId}:mods:${dayKey}`;
	const templatesKey = `user:${userId}:templates`;
	const uidStr = String(userId);

	// 1. Update Data (Mark as Deleted)
	if (taskType === 'custom') {
		const added = await getRedisJSON(redis, dailyModsKey, 'added', []);
		const updatedAdded = added.filter((t) => t.id !== taskId);
		await redis.hset(dailyModsKey, { added: JSON.stringify(updatedAdded) });
		await redis.expire(dailyModsKey, 259200);
	} else if (taskType === 'template') {
		const mods = await getRedisJSON(redis, dailyModsKey, 'mods', {});
		if (!mods[templateId]) mods[templateId] = {};
		mods[templateId].deleted = true;
		delete mods[templateId].done;
		await redis.hset(dailyModsKey, { mods: JSON.stringify(mods) });
		await redis.expire(dailyModsKey, 259200);
	}

	// 2. Update Notification Index
	if (start) {
		const indexKey = `schedule_index:${dayKey}:${start}`;

		// CHECK BEFORE REMOVE
		// We just deleted the task in the DB. Now fetch the list to see what remains.
		const [templatesData, dailyModsData] = await redis.pipeline().hgetall(templatesKey).hgetall(dailyModsKey).exec();

		// serverGetDayTasks automatically filters out "deleted: true" items
		const allTasks = serverGetDayTasks(dayKey, templatesData, dailyModsData);

		// Are there ANY active tasks left at this time?
		const remaining = allTasks.filter((t) => t.start === start && !t.done);

		if (remaining.length === 0) {
			await redis.srem(indexKey, uidStr);
			console.log(`[Delete] Clear: No tasks left at ${start}. Removed user.`);
		} else {
			console.log(`[Delete] Hold: ${remaining.length} tasks still active at ${start}.`);
		}
	}
}

/**
 * Clears completed or missed tasks for a given day.
 */
async function handleClearDay(userId, { dayKey, type }, env) {
	const redis = Redis.fromEnv(env);
	const templatesKey = `user:${userId}:templates`;
	const dailyModsKey = `user:${userId}:mods:${dayKey}`;

	const [templatesData, dailyModsData] = await redis.pipeline().hgetall(templatesKey).hgetall(dailyModsKey).exec();

	if (!templatesData && !dailyModsData) return;

	const allTasks = serverGetDayTasks(dayKey, templatesData, dailyModsData);

	const now = new Date();
	const nowMin = now.getHours() * 60 + now.getMinutes();
	let tasksToClear;

	if (type === 'completed') {
		tasksToClear = allTasks.filter((t) => t.done);
	} else if (type === 'missed') {
		tasksToClear = dayKey === toKey(now) ? allTasks.filter((t) => !t.done && timeToMin(t.end) < nowMin) : allTasks.filter((t) => !t.done);
	} else {
		return; // Invalid type
	}

	if (tasksToClear.length === 0) return;

	const customIdsToClear = new Set(tasksToClear.filter((t) => t.type === 'custom').map((t) => t.id));
	const templateIdsToClear = new Set(tasksToClear.filter((t) => t.type === 'template').map((t) => t.templateId));

	// Safely read the current state before modifying
	const mods = safeJsonParse(dailyModsData?.mods, {});
	const added = safeJsonParse(dailyModsData?.added, []);

	for (const templateId of templateIdsToClear) {
		if (!mods[templateId]) mods[templateId] = {};
		mods[templateId].deleted = true;
		delete mods[templateId].done;
	}

	const updatedAdded = added.filter((t) => !customIdsToClear.has(t.id));

	// --- THE FIX: Use a pipeline to update data and set TTL ---
	const pipeline = redis.pipeline();
	pipeline.hset(dailyModsKey, {
		mods: JSON.stringify(mods),
		added: JSON.stringify(updatedAdded),
	});
	pipeline.expire(dailyModsKey, 259200); // Set TTL to 72 hours
	await pipeline.exec();
}

// REPLACE your entire old handleUpdateRoutines function with this definitive one.
async function handleUpdateRoutines(userId, { day, tasks }, env) {
	const redis = Redis.fromEnv(env);
	const templatesKey = `user:${userId}:templates`;

	console.log(`[handleUpdateRoutines] User: ${userId}, Day: ${day}`);

	// 1. Get the OLD routine for this day.
	const oldTemplateJson = await redis.hget(templatesKey, day);
	const oldTasks = safeJsonParse(oldTemplateJson, []);

	// 2. Save the NEW routine.
	await redis.hset(templatesKey, { [day]: JSON.stringify(tasks) });

	// 3. Determine if this change affects Today or Tomorrow
	const nowIST = new Date(new Date().getTime() + 5.5 * 60 * 60 * 1000);
	const tomorrowIST = new Date(nowIST.getTime() + 24 * 60 * 60 * 1000);
	const todayDayOfWeek = nowIST.getDay();
	const tomorrowDayOfWeek = tomorrowIST.getDay();

	// If the edited day isn't Today or Tomorrow, we don't care about indexing right now.
	if (parseInt(day, 10) !== todayDayOfWeek && parseInt(day, 10) !== tomorrowDayOfWeek) {
		return;
	}

	const dayKey = parseInt(day, 10) === todayDayOfWeek ? toKey(nowIST) : toKey(tomorrowIST);
	const isToday = parseInt(day, 10) === todayDayOfWeek;

	const pipeline = redis.pipeline();
	const nowMin = nowIST.getHours() * 60 + nowIST.getMinutes();
	let commandsAdded = 0;

	const oldTasksMap = new Map(oldTasks.map((task) => [task.id, task]));
	const newTasksMap = new Map(tasks.map((task) => [task.id, task]));

	// 4. DELETIONS: Always perform deletions.
	// If the Cron already ran and indexed these, we must remove them.
	// If the Cron hasn't run, removing a non-existent key is harmless.
	for (const [taskId, oldTask] of oldTasksMap.entries()) {
		if (!newTasksMap.has(taskId)) {
			const indexKey = `schedule_index:${dayKey}:${oldTask.start}`;
			pipeline.srem(indexKey, userId);
			commandsAdded++;
		}
	}

	// 5. ADDITIONS: Only index if it is TODAY.
	// If it is Tomorrow, we skip and let the Night Shift Cron handle it.
	if (isToday) {
		for (const [taskId, newTask] of newTasksMap.entries()) {
			if (!oldTasksMap.has(taskId)) {
				const taskMin = timeToMin(newTask.start);
				// Only index if time hasn't passed
				if (taskMin >= nowMin) {
					const indexKey = `schedule_index:${dayKey}:${newTask.start}`;
					pipeline.sadd(indexKey, userId);
					pipeline.expire(indexKey, 172800);
					commandsAdded += 2;
				}
			}
		}
	} else {
		console.log(`[handleUpdateRoutines] Skipping indexing for Tomorrow (Permanent Tasks). Relying on Night Shift Cron.`);
	}

	if (commandsAdded > 0) {
		await pipeline.exec();
	}
}

