// --- AUTHENTICATION HANDLERS ---

function handleGoogleLogin(env) {
	const authUrl = new URL('https://accounts.google.com/o/oauth2/v2/auth');
	authUrl.searchParams.set('client_id', env.GOOGLE_CLIENT_ID);
	authUrl.searchParams.set('redirect_uri', `https://schedule-max.anuragfr.workers.dev/auth/google/callback`); // IMPORTANT: USE YOUR ACTUAL WORKER URL
	authUrl.searchParams.set('response_type', 'code');
	authUrl.searchParams.set('scope', 'https://www.googleapis.com/auth/userinfo.profile https://www.googleapis.com/auth/userinfo.email');
	authUrl.searchParams.set('prompt', 'select_account');
	return Response.redirect(authUrl.toString(), 302);
}

async function handleGoogleCallback(request, env) {
	const url = new URL(request.url);
	const code = url.searchParams.get('code');
	if (!code) return new Response('Missing code', { status: 400 });

	// 1. Exchange code for token
	const tokenResponse = await fetch('https://oauth2.googleapis.com/token', {
		method: 'POST',
		headers: { 'Content-Type': 'application/json' },
		body: JSON.stringify({
			code,
			client_id: env.GOOGLE_CLIENT_ID,
			client_secret: env.GOOGLE_CLIENT_SECRET,
			redirect_uri: `https://schedule-max.anuragfr.workers.dev/auth/google/callback`, // Verify this matches your deployed URL
			grant_type: 'authorization_code',
		}),
	});
	const tokenData = await tokenResponse.json();
	if (!tokenData.access_token) return new Response('Failed to get token', { status: 500 });

	// 2. Get user info from Google
	const userResponse = await fetch('https://www.googleapis.com/oauth2/v2/userinfo', {
		headers: { Authorization: `Bearer ${tokenData.access_token}` },
	});
	const userData = await userResponse.json();

	// 3. Create a session token (JWT)
	const sessionToken = await createJWT(
		{ user: { id: userData.id, email: userData.email, name: userData.name, picture: userData.picture } },
		env.JWT_SECRET,
	);

	const redis = Redis.fromEnv(env);

	// 4. Add user to our master list for the daily cron job
	await redis.sadd('users', userData.id);

	// ==================================================================
	// 5. NEW: Store Profile Data in Database
	// ==================================================================
	// This saves/updates the name, email, and picture every time they login.
	// This does NOT overwrite 'onboardingComplete' or 'telegramId' if they already exist.
	await redis.hset(`user:${userData.id}:profile`, {
		email: userData.email,
		name: userData.name,
		picture: userData.picture,
	});
	// ==================================================================

	// Redirect to the main app with the session cookie set
	return new Response(null, {
		status: 302,
		headers: {
			Location: '/',
			'Set-Cookie': `session=${sessionToken}; Path=/; HttpOnly; Secure; SameSite=Lax; Max-Age=2592000`, // 30 days
		},
	});
}

// --- JWT & SESSION HELPERS (using Web Crypto API) ---

async function getSession(cookie, secret) {
	if (!cookie || !cookie.includes('session=')) return null;
	const sessionCookie = cookie.split(';').find((c) => c.trim().startsWith('session='));
	if (!sessionCookie) return null;
	const token = sessionCookie.split('=')[1];
	return await verifyJWT(token, secret);
}

async function createJWT(payload, secret) {
	const header = { alg: 'HS256', typ: 'JWT' };
	const encodedHeader = btoa(JSON.stringify(header)).replace(/=/g, '').replace(/\+/g, '-').replace(/\//g, '_');
	const encodedPayload = btoa(JSON.stringify(payload)).replace(/=/g, '').replace(/\+/g, '-').replace(/\//g, '_');

	const key = await crypto.subtle.importKey('raw', new TextEncoder().encode(secret), { name: 'HMAC', hash: 'SHA-256' }, false, ['sign']);
	const signature = await crypto.subtle.sign('HMAC', key, new TextEncoder().encode(`${encodedHeader}.${encodedPayload}`));

	const encodedSignature = btoa(String.fromCharCode(...new Uint8Array(signature)))
		.replace(/=/g, '')
		.replace(/\+/g, '-')
		.replace(/\//g, '_');
	return `${encodedHeader}.${encodedPayload}.${encodedSignature}`;
}

async function verifyJWT(token, secret) {
	try {
		const [header, payload, signature] = token.split('.');
		const key = await crypto.subtle.importKey('raw', new TextEncoder().encode(secret), { name: 'HMAC', hash: 'SHA-256' }, false, [
			'verify',
		]);
		const isValid = await crypto.subtle.verify(
			'HMAC',
			key,
			new Uint8Array(
				atob(signature.replace(/-/g, '+').replace(/_/g, '/'))
					.split('')
					.map((c) => c.charCodeAt(0)),
			),
			new TextEncoder().encode(`${header}.${payload}`),
		);

		if (isValid) {
			return JSON.parse(atob(payload.replace(/-/g, '+').replace(/_/g, '/')));
		}
	} catch (e) {}
	return null;
}

