/**
 * Handles requests for the Service Worker script.
 */
function handleServiceWorkerRequest(request) {
	const serviceWorkerScript = `
              const CACHE_NAME = 'schedule-max-cache-v3';
              // These are the essential files for the app shell to work offline.
              const urlsToCache = [
                '/',
                '/manifest.json',
                '/icon-192x192.png',
                '/icon-512x512.png'
              ];

              // Install event: open cache and add app shell files.
              self.addEventListener('install', event => {
                event.waitUntil(
                  caches.open(CACHE_NAME)
                    .then(cache => {
                      return cache.addAll(urlsToCache);
                    })
                );
              });

              // Fetch event: serve from cache first (cache-first strategy).
              self.addEventListener('fetch', event => {
                // Do not cache API requests.
                if (event.request.method !== 'GET' || event.request.url.includes('/api/')) {
                  return; // Let the browser handle it (network request).
                }

                event.respondWith(
                  caches.match(event.request)
                    .then(response => {
                      // If the resource is in the cache, return it.
                      if (response) {
                        return response;
                      }
                      // Otherwise, fetch from the network.
                      return fetch(event.request);
                    })
                );
              });
            `;

	return new Response(serviceWorkerScript, {
		headers: { 'Content-Type': 'application/javascript; charset=utf-8' },
	});
}

/**
 * Handles requests for the icon by decoding the embedded Base64 string.
 */
function handleIconRequest(b64) {
	// Helper function to convert Base64 string to a byte array (Uint8Array)
	const byteCharacters = atob(b64);
	const byteNumbers = new Array(byteCharacters.length);
	for (let i = 0; i < byteCharacters.length; i++) {
		byteNumbers[i] = byteCharacters.charCodeAt(i);
	}
	const byteArray = new Uint8Array(byteNumbers);

	return new Response(byteArray, {
		headers: { 'Content-Type': 'image/png' },
	});
}
